package come.example.root.moviemvp.model;import android.os.Parcel;import android.os.Parcelable;import com.google.gson.annotations.SerializedName;public class Movie implements Parcelable{    @SerializedName("id")    private int id;    @SerializedName("title")    private String title;    @SerializedName("release_date")    private String release;    @SerializedName("overview")    private String overview;    @SerializedName("poster_path")    private String poster;    @SerializedName("backdrop_path")    private String backdrop;    public Movie(){    }    public Movie(int id, String title, String release, String overview, String poster, String backdrop) {        this.id = id;        this.title = title;        this.release = release;        this.overview = overview;        this.poster = poster;        this.backdrop = backdrop;    }    public void setId(int id) {        this.id = id;    }    public void setTitle(String title) {        this.title = title;    }    public void setRelease(String release) {        this.release = release;    }    public void setOverview(String overview) {        this.overview = overview;    }    public void setPoster(String poster) {        this.poster = poster;    }    public void setBackdrop(String backdrop) {        this.backdrop = backdrop;    }    public int getId() {        return id;    }    public String getTitle() {        return title;    }    public String getRelease() {        return release;    }    public String getOverview() {        return overview;    }    public String getPoster() {        return poster;    }    public String getBackdrop() {        return backdrop;    }    protected Movie(Parcel in) {        id = in.readInt();        title = in.readString();        release = in.readString();        overview = in.readString();        poster = in.readString();        backdrop = in.readString();    }    public static final Parcelable.Creator<Movie> CREATOR = new Parcelable.Creator<Movie>() {        @Override        public Movie createFromParcel(Parcel in) {            return new Movie(in);        }        @Override        public Movie[] newArray(int size) {            return new Movie[size];        }    };    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeInt(id);        dest.writeString(title);        dest.writeString(release);        dest.writeString(overview);        dest.writeString(poster);        dest.writeString(backdrop);    }}