package come.example.root.moviemvp.model;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;import retrofit2.http.GET;public class MovieService {    private Retrofit retrofit = null;    public ServiceAPI getAPIKey(){        String url = "https://api.themoviedb.org/3/";        if (retrofit == null){            retrofit = new Retrofit                    .Builder()                    .baseUrl(url)                    .addConverterFactory(GsonConverterFactory.create())                    .build();        }        return retrofit.create(ServiceAPI.class);    }}