package come.example.root.moviemvp.model;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public class MovieService {    private Retrofit retrofit;    public ServiceAPI getAPIKey(){        String url = "https://api.themoviedb.org/3/";        String API_KEY = "233b568bea8fb27357da8dd3df2fc37f";        if (retrofit == null){            retrofit = new Retrofit                    .Builder()                    .baseUrl(url+"?api_key="+API_KEY+"&language=en-US")                    .addConverterFactory(GsonConverterFactory.create())                    .build();        }        return retrofit.create(ServiceAPI.class);    }}