package come.example.root.moviemvp.presenter;import java.util.List;import come.example.root.moviemvp.model.Movie;import come.example.root.moviemvp.model.MovieRespons;import come.example.root.moviemvp.model.MovieService;import come.example.root.moviemvp.views.MainView;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;public class MoviePresenter {    private MovieService service;    private MainView view;    public MoviePresenter(MainView view) {        this.view = view;        if (service == null){            this.service = new MovieService();        }    }    public void getNowPlayingMovies(){        service.getAPIKey().getMovies().enqueue(new Callback<MovieRespons>() {            @Override            public void onResponse(Call<MovieRespons> call, Response<MovieRespons> response) {                MovieRespons respons = response.body();                if (respons != null && respons.getMovies() != null){                    List<Movie> result = respons.getMovies();                    view.getMovies(result);                }            }            @Override            public void onFailure(Call<MovieRespons> call, Throwable t) {                try {                    throw new InterruptedException("Error has been detected !!");                } catch (InterruptedException e){                    e.printStackTrace();                }            }        });    }}