package come.example.root.moviemvp.views;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.ImageView;import android.widget.TextView;import com.squareup.picasso.Picasso;import come.example.root.moviemvp.R;import come.example.root.moviemvp.model.Movie;public class MovieDetailActivity extends AppCompatActivity {    public static final String MOVIE_DATA = "movie_data";    private TextView title, release, overview;    private ImageView backdrop;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_movie_detail);        title = (TextView)findViewById(R.id.detail_title);        release = (TextView)findViewById(R.id.detail_release);        overview = (TextView)findViewById(R.id.detail_overview);        backdrop = (ImageView)findViewById(R.id.backdrop);        Movie movie = getIntent().getParcelableExtra(MOVIE_DATA);        title.setText(movie.getTitle());        overview.setText(movie.getOverview());        release.setText(movie.getRelease());        Picasso.with(this)                .load("http://image.tmdb.org/t/p/w500/"+movie.getBackdrop())                .placeholder(this.getResources().getDrawable(R.mipmap.ic_launcher))                .error(this.getResources().getDrawable(R.mipmap.ic_launcher))                .into(backdrop);    }}